definition.script:
node ("agent-node-label") {
    def DockerImage = "midproj"
    stage("git") { 
       git credentialsId: 'mycred',
       url: 'https://github.com/michalazarovitz/crud-application-using-flask-and-mysql.git'
    }

    stage("build docker") {
        customImage = docker.build("michalazarovitz/midproj")
    }

    stage("test") {
        sh 'docker images'
    }

    stage("push to Docker Hub") {
        withDockerRegistry(credentialsId: 'mycred') {
            customImage.push()
        }
    }

     stage("mysql") { // Create table 
        sh """
        cd /home/ec2-user/
        scp -o StrictHostKeyChecking=no -i Mid-proj.pem /home/ec2-user/jenkins/workspace/midproj_pipeline/database/crud_flask.sql ubuntu@$MYSQL_HOST:/home/ubuntu/
        ssh -o StrictHostKeyChecking=no -i Mid-proj.pem ubuntu@$MYSQL_HOST <<EOF
        sudo mysql --user='$MYSQL_USERNAME' --password='$MYSQL_PASSWORD' 
        CREATE DATABASE crud_flask;
        use crud_flask;
        source /home/ubuntu/crud_flask.sql;
        exit
        EOF
        """
    }

    stage("deploy") { // Deploy the app
        sh """
        cd /home/ec2-user/
        ssh -o StrictHostKeyChecking=no -i Mid-proj.pem ubuntu@k8sipadress <<EOF
        kubectl create configmap mysqlconfig --from-literal=MYSQL_HOST=$MYSQL_HOST --from-literal MYSQL_USER=dev
        cd k8sfiles
        kubectl create secret generic db-pass --from-file=MYSQL_PWD
        kubectl apply -f /home/ubuntu/k8sfiles/deployment.yml
        exit
        EOF
        """
    }
}
